import React,{useState,useRef} from 'react'
import {Button ,Container} from "react-bootstrap"
import 'bootstrap/dist/css/bootstrap.min.css';


function Tutorial() {
    const[count,setCount]=useState(1)   
    const sum = () =>{
        setCount(prevalue=>{
            setCount(count + 1)
            if (count === 3) {
                let nextBtn =document.querySelector(".summed");
                nextBtn.setAttribute("disabled", "")
               
                
                
                
                
            }
        })

       
    }
    const sub = ()=>{
        setCount(prevalue=>{
            if(prevalue <= 4 && prevalue > 0){
                let nextBtn =document.querySelector(".summed");
                nextBtn.removeAttribute("disabled", "")                
                setCount(count -1)
                if (prevalue == 2) {
                    let subBtn =document.querySelector(".subtract");
                    subBtn.setAttribute("disabled", "")
                }

            }           

            
        } )
    }




  return (
    <div>
        <div style={{display:"flex"}}>
        <Button onClick={sum}  className="summed">+</Button>
            {count}
        <Button onClick={sub} className="subtract">-</Button>
        </div>
        
    </div>
  )
}

export default Tutorial
