import React, {useState} from "react";
import { SortableContainer, SortableElement } from "react-sortable-hoc";
import {Container,Button} from "react-bootstrap"
import 'bootstrap/dist/css/bootstrap.min.css';
import arrayMove from "array-move";
import useStore from "../store";
import BaseImage from "./BaseImage"
import {useNavigate,Link,} from "react-router-dom"
import Canvas from "./Canvas";
import "../styles.css"



const SortableItem = SortableElement(({ region, sortIndex, onRemove }) => {
  return (
    <div
      className="region"
      style={{
        boxShadow: `0 0 5px ${region.color}`,
        border: `1px solid ${region.color}`
      }}
    >
      Region #{region.id}
      <button
        onClick={() => {
          onRemove(sortIndex);
        }}
      >
        Delete
      </button>      
    </div>
  );
});


const SortableList = SortableContainer(({ items, onRemove }) => {
  return (
    <div className="regions-list">
      {items.map((region, index) => (
        <SortableItem
          key={`item-${region.id}`}
          index={index}
          region={region}
          onRemove={onRemove}
          sortIndex={index}
        />

      ))}      
    </div>
  );
});


function RegionList ()  {
  const regions = useStore(s => s.regions);
  const setRegions = useStore(s => s.setRegions); 
  const [showCoordinates, setShowCoordinates] = useState(false)

  let clicked = false;
  const navigate = useNavigate()
  function back() {
    navigate("/")
  }



  const displayCordinates = () => {
    setShowCoordinates(!showCoordinates)
  }

  const listCoordinates = () =>(
  <div>


  </div>)


const [imageSrc, setImgSrc] = useState(1);

  const incrementImg = () => {
    if (imageSrc <= 7)
      setImgSrc(imageSrc + 1);
      let backBtn =document.querySelector(".sub");            
      backBtn.removeAttribute("disabled", "")
      if (imageSrc === 7) {
        let nextBtn =document.querySelector(".sum");
        nextBtn.innerHTML="Continue";
        backBtn.removeAttribute("disabled", "")

      }

      //let backBtn =document.querySelector(".sub");            
      //backBtn.removeAttribute("disabled", "")
  }

  const decrementImg = () => {    
    if (imageSrc >= 0) {
      let nextBtn =document.querySelector(".sum");      
        nextBtn.innerHTML="Next";                   
        setImgSrc(imageSrc - 1);

        if (imageSrc === 2) {

        }
    }



  }

  const clearRegions = () => {
    setTimeout(() => {
      setRegions([]);
    }, 1000)

  }

  const wrappedNextFunc = async () => {
    displayCordinates();
    incrementImg()   
    clearRegions()
    /*
    const response = await axios.post('/coordinates', {data: JSON.stringify(regions)});
    if (response.data.status === 200) {
      incrementImg(); //Next Image
      clearRegions(); // Clear current regions on the screen
    }
    */

  }


  return (
    <div>
       <div className="Task-container wrapper2">          
              <Canvas imageSrc={imageSrc} /> 

        </div>

      <SortableList items={regions} onSortEnd={({ oldIndex, newIndex }) => {setRegions(arrayMove(regions, oldIndex, newIndex))}}   onRemove={index => {regions.splice(index, 1);setRegions(regions.concat()); }}
      />  
      <div className="d-flex container justify-content-center mt-5">      

      <Button onClick={decrementImg} className="previous sub">&laquo; Previous</Button> 
      <div style={{width:"100px"}}></div>  
      <Button onClick={wrappedNextFunc} className="next sum">Next &raquo;</Button>
      {showCoordinates && listCoordinates()} 

      </div>





    </div>

  );

};

export default RegionList
