import React, { useState,useRef, useEffect } from "react";
import { Container, FormCheck } from "react-bootstrap";
import FinalSlide from "./FinalSlide";
import SlideShow from "./SlideShow";
import SlideTwo from "./SlideTwo";
import Task from "../Task";
import reactDom from "react-dom";
import { useNavigate } from "react-router-dom";

function Test() {
  const [page, setPage] = useState(0);
  const [TestAnswers, setTestanswers] = useState({
    Q1: "",
    Q2: "",
    Q3: "",
  });
  

  const TestTitle = [
    "Select which image has tumor",
    "Select which image has tumor",
    "Select which image is correctly annotated",
  ];

  const PageDisplay = () => {
    if (page === 0) {
      return (
        <SlideShow
          TestAnswers={TestAnswers}
          setTestanswers={setTestanswers}
         
        />
      );
    } else if (page === 1) {
      return (
        <SlideTwo
          TestAnswers={TestAnswers}
          setTestanswers={setTestanswers}
        ></SlideTwo>
      );
    } else if (page === 2) {
      return (
        <FinalSlide
          TestAnswers={TestAnswers}
          setTestanswers={setTestanswers}
        ></FinalSlide>
      );
    }
  };
  const navigate = useNavigate();

  

  return (
    <Container fluid>
      <div className="test-container">
        <div className="header">
          <h1>Testing</h1>
          <h5>{TestTitle[page]}</h5>
        </div>
        <div className="body" style={{ flex: "60%" }}>
          {PageDisplay()}
        </div>
        <div className="footer">
          <button
            className="previous"
            disabled={page === 0}
            onClick={() => {
              setPage((currentPage) => currentPage - 1);
            }}
          >
            &laquo;Previous
          </button>
          <button
            className="next"
            disabled={page === TestTitle.length}
            onClick={() => {
              if (page === TestTitle.length - 1) { 
                 if (TestAnswers.Q1 === "A" && TestAnswers.Q2 === "C" && TestAnswers.Q3 === "E") {
                   navigate("/Task")
                 } else {
                   <h5>Select one</h5>
                 }
                               
                
                
              } else {                
                  const buttons = document.querySelectorAll("input[type='radio']");
                  buttons.forEach((button)=>{
                    if(!button.checked) {
                      button.setAttribute("required","")                     
                     
                    }

                    if (button.checked) {
                      setPage((currentPage) => currentPage + 1);
                    }
                  })
                
               
              }
            }}
          >
            {page === TestTitle.length - 1 ? "Continue" : "Next"}
          </button>
        </div>
      </div>
    </Container>
  );
}

export default Test;
