import React from "react";
import { SortableContainer, SortableElement } from "react-sortable-hoc";
import arrayMove from "array-move";

import useStore from "../store";

const SortableItem = SortableElement(({ region, sortIndex, onRemove }) => {
  return (
    <div
      className="region"
      style={{
        boxShadow: `0 0 5px ${region.color}`,
        border: `1px solid ${region.color}`
      }}
    >
      Region #{region.id}
      <button
        onClick={() => {
          onRemove(sortIndex);
        }}
      >
        Delete
      </button>      
    </div>
  );
});


const SortableList = SortableContainer(({ items, onRemove }) => {
  return (
    <div className="regions-list">
      {items.map((region, index) => (
        <SortableItem
          key={`item-${region.id}`}
          index={index}
          region={region}
          onRemove={onRemove}
          sortIndex={index}
        />

      ))}      
    </div>
  );
});

function RegionList ()  {
  const regions = useStore(s => s.regions);
  const setRegions = useStore(s => s.setRegions); 


    /*function cordinates (){
    regions.map((item)=>{
    for (let cord in item) {
      if (cord === "points") {
        console.log(item["id"] ,item[cord])
      }
    }
  })
 */ 

  /**
   * const regionsJson = JSON.stringify(regions);
   * const data = await axios.post('localhost:7000/region', regionsJSON);
   */

  const displayCordinates = () => {
    return(<div>
            <ul>Cordinates:{regions.map((item,index)=>{
              return <li key={index}>
                      <li>{item.id}</li>        
                        {item.points.map(point => {
                      return <div key={index}>x:{point.x} y:{point.y}</div>})}
                      </li>
                })}</ul>

           </div>)
  }

  return (
    <div>
    <SortableList items={regions} onSortEnd={({ oldIndex, newIndex }) => {setRegions(arrayMove(regions, oldIndex, newIndex))}}   onRemove={index => {regions.splice(index, 1);setRegions(regions.concat()); }}
    />
    <button onClick={displayCordinates}>Cordinates</button>


    </div>

  );

};

export default RegionList
