import React,{useState} from "react";
import {Button } from "react-bootstrap";
import SlideTwo from "./SlideTwo";
import FinalSlide from "./FinalSlide";
import { useNavigate } from "react-router-dom";

function SlideShow() {

  const [page,setPage] = useState(0);
  const TestTitle = ["Select which image has tumor", "Select which image has lungs" , "Select which image is correct annotated"]
  //let navi = useNavigate();

  if ( page === 1 ) {
    return <SlideTwo></SlideTwo>
  } else if ( page ===2 ) {
    navi( "/FinalSlide" )
  } else {
    navi( "/SlideShow" )
  }



  return (
    <div className="Container" style={{overflow:"hidden"}}>      
        <div className="text-center mt-4">
          <h2>Take a Test</h2>
        </div>
        <div className="header text-center mt-4">{TestTitle[page]}</div>
        <div className="Container">
          <div className="row justify-content-around py-4">
            <div className="col-sm-6 col-md-6 col-lg-5 col-xxl-6 col-xl-5">
              <img src="/Testing/Test1_Tumor.jpg" className="card-img-size img-fluid" ></img>
              <div className="d-flex justify-content-center my-5">
                <input type="radio" className="ms-3" name="" value="" ></input>
              </div>
            </div>
            <div className="col-sm-6 col-md-6 col-lg-5 col-xxl-6 col-xl-5">
              <img src="/Testing/Test1_No_Tumor.jpg" className="card-img-size img-fluid" ></img>
              <div className="d-flex justify-content-center my-5">
                <input type="radio" className="ms-3" name="" value=""></input>
              </div>
            </div>
          </div>
        </div>       
        <div className="d-flex justify-content-center">
        <button className="previous" disabled={page === 0}
        onClick={()=>{
          setPage((currentPage)=>currentPage - 1);
        }} > &laquo;Previous</button>
        <div className="mx-4"></div>
        <button className="next" disabled={page === TestTitle.length -1}
        onClick={()=>{
          setPage((currentPage)=>currentPage + 1);
        }} 
        >Next&raquo;</button>
        </div>
        
    </div>
  );
}

export default SlideShow;
