import React,{useState,useRef} from 'react'
import {Button ,Container} from "react-bootstrap"
import 'bootstrap/dist/css/bootstrap.min.css';


function Tutorial() {
    const[count,setCount]=useState(1)
    const [subcount,subSetCount] = useState(4)
    
      
    const sum = () =>{
        setCount(prevalue=>{            
            setCount(prevalue + 1)          
                                                         
            let lmg = document.querySelector(".img")                      
                lmg.src= "/Slideshow/image_" + count +  ".gif"; 
                console.log(count)  
            if (count === 3) {
                let nextBtn =document.querySelector(".summed");
                nextBtn.setAttribute("disabled", "")
                            
                let subBtn =document.querySelector(".subtract");
                subBtn.removeAttribute("disabled", "")
                  
               
                
                
                
                
            }
        })

       
    }
    const sub = ()=>{
        subSetCount(prevalue=>{
           subSetCount(count -1 )
           console.log(count)
            
        } )
    }




  return (
    <div>
        <div style={{display:"flex"}}>
        <Button onClick={sum}  className="summed">+</Button>
        <img src="/Slideshow/image_0.gif" className="img-fluid img" style={{width:"200px","height":"200px"}}></img>
        <Button onClick={sub} className="subtract">-</Button>
        </div>
        <p>{count}</p>
        
    </div>
  )
}

export default Tutorial
