import React, { Component, useState, useRef, useEffect } from "react";
import { Form, Button, Container } from "react-bootstrap";
import { useNavigate, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import ReactDOM from "react-dom";

function Questionaries() {
  const [Questions, setQuestions] = useState({
    Q1: "",
    Q2: "",
    Q3: "",
    Q4: "",
  });

  const handleAnswers = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    setQuestions({ ...Questions, [name]: value });
  };

  function submitForm(e) {
    if (Object.entries(Questions).length > 0) {
      e.preventDefault();
      console.log(Questions);
      navigate("/Description");
    }
  }

  function check() {
    const buttons = document.querySelectorAll("input[type='radio']");
    buttons.forEach((button) => {
      if (!button.checked) {
        button.setAttribute("required", "");
      }
    });
  }

  const navigate = useNavigate();

  return (
    <>
      <Container
        className="d-flex align-items-center justify-content-center "
        style={{ minHeight: "100vh" }}
      >
        <div className="w-100" style={{ maxWidth: "1000px" }}>
          <div className="form-container">
            <form
              onSubmit={submitForm}
              style={{ display: "flex", flexDirection: "column" }}
            >
              <legend className="mb-3">
                Please fill out this Questionaries?
              </legend>
              <div className="mb-3">
                <label className="mb-3">What is your Gender</label>
                <p>
                  <input
                    type="radio"
                    name="Q1"
                    value="Male"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  Male
                </p>
                <p>
                  <input
                    type="radio"
                    name="Q1"
                    value="Female"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  Female
                </p>
                <p>
                  <input
                    type="radio"
                    name="Q1"
                    value="Other"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  Other
                </p>
              </div>
              <div className="mb-3">
                <label className="mb-3">What is your age?</label>
                <p>
                  <input
                    type="radio"
                    name="Q2"
                    value="18-25"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  18-25
                </p>
                <p>
                  <input
                    type="radio"
                    name="Q2"
                    value="26-35"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  26-35
                </p>
                <p>
                  <input
                    type="radio"
                    name="Q2"
                    value="36-45"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  36-45
                </p>
                <p>
                  <input
                    type="radio"
                    name="Q2"
                    value="older than 45"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  45-60
                </p>
              </div>
              <div className="mb-3">
                <label className="mb-3">What is your level of education?</label>
                <p>
                  <input
                    type="radio"
                    name="Q3"
                    value="No schooling"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  No schooling
                </p>
                <p>
                  <input
                    type="radio"
                    name="Q3"
                    value="High School"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  High School
                </p>
                <p>
                  <input
                    type="radio"
                    name="Q3"
                    value="College"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  College
                </p>
                <p>
                  <input
                    type="radio"
                    name="Q3"
                    value="Bachelors Degree"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  Bachelors Degree
                </p>
                <p>
                  <input
                    type="radio"
                    name="Q3"
                    value="Master Degree"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  Master Degree
                </p>
                <p>
                  <input
                    type="radio"
                    name="Q3"
                    value="Doctorate Degree"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  Doctorate Degree
                </p>
              </div>
              <div className="mb-3">
                <label className="mb-3">
                  Are you a native speaker of English?
                </label>
                <p>
                  <input
                    type="radio"
                    name="Q4"
                    value="Yes"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  Yes
                </p>
                <p>
                  <input
                    type="radio"
                    name="Q4"
                    value="No"
                    onChange={handleAnswers}
                    className="me-2"
                  />
                  No
                </p>
              </div>

              <button
                type="submit"
                className="mt-4"
                id="submitBtn"
                className=""
                onClick={check}
              >
                Submit
              </button>
            </form>
          </div>
        </div>
      </Container>
    </>
  );
}

export default Questionaries;

