import React, {useState} from "react";
import { SortableContainer, SortableElement } from "react-sortable-hoc";
import {Container,Button} from "react-bootstrap"
import 'bootstrap/dist/css/bootstrap.min.css';
import arrayMove from "array-move";
import useStore from "../store";
import BaseImage from "./BaseImage"
import {useNavigate,Link,} from "react-router-dom"
import Canvas from "./Canvas";



const SortableItem = SortableElement(({ region, sortIndex, onRemove }) => {
  return (
    <div
      className="region"
      style={{
        boxShadow: `0 0 5px ${region.color}`,
        border: `1px solid ${region.color}`
      }}
    >
      Region #{region.id}
      <button
        onClick={() => {
          onRemove(sortIndex);
        }}
      >
        Delete
      </button>      
    </div>
  );
});


const SortableList = SortableContainer(({ items, onRemove }) => {
  return (
    <div className="regions-list">
      {items.map((region, index) => (
        <SortableItem
          key={`item-${region.id}`}
          index={index}
          region={region}
          onRemove={onRemove}
          sortIndex={index}
        />

      ))}      
    </div>
  );
});


function RegionList ()  {
  const regions = useStore(s => s.regions);
  const setRegions = useStore(s => s.setRegions); 
  const [showCoordinates, setShowCoordinates] = useState(false)

  let clicked = false;
  const navigate = useNavigate()
  function back() {
    navigate("/")
  }



  const displayCordinates = () => {
    setShowCoordinates(!showCoordinates)
  }

  const listCoordinates = () =>(
  <div>
      <ul>Cordinates:
        {regions.map((item,index)=>(
        <li key={index}>
                <div>Id#{item.id}</div>        
                  {item.points.map((point, pKey) =>(
                  <div key={pKey}>x:{point.x} y:{point.y}</div>))}
        </li>))}
      </ul>

  </div>)


const [imageSrc, setImgSrc] = useState(1);

  const incrementImg = () => {
    if (imageSrc <= 4)
      setImgSrc(imageSrc + 1);
  }

  const decrementImg = () => {
    if (imageSrc >= 0)
      setImgSrc(imageSrc - 1);
  }


  return (
    <div>
       <div className="Task-container col-8">
              <Canvas imageSrc={imageSrc} />        
              </div>

      <SortableList items={regions} onSortEnd={({ oldIndex, newIndex }) => {setRegions(arrayMove(regions, oldIndex, newIndex))}}   onRemove={index => {regions.splice(index, 1);setRegions(regions.concat()); }}
      />  
      <div className="d-flex container">      
      <div style={{width:"100px"}}></div>   
      <Button onClick={back && decre} className="next">Previous &raquo;</Button> 
      <Button onClick={displayCordinates}>Next Image</Button>
      {showCoordinates && listCoordinates()} 

      </div>





    </div>

  );

};

export default RegionList
