import React,{useState,useRef} from 'react'
import {Button ,Container} from "react-bootstrap"
import 'bootstrap/dist/css/bootstrap.min.css';


function Tutorial() {
    const[count,setCount]=useState(0)
    //const [subcount,subSetCount] = useState(2)
    function updateSum() {
        for(let i = 0;i < count;i++) {
            increment()
        }
    }

    function increment () {
        setCount(pre => {
            setCount(pre + 1)
            let lmg = document.querySelector(".img")                      
            let g = `/Slideshow/image_${pre+1}`+`.gif`
            lmg.src=g;
            if (count ===2) {
                console.log("disable here next ")
            }
           
            
        })

        
    }


    function updateSub() {
        for(let i = 0;i < count;i--) {
            decrement()
            
        }
    }

    function decrement() {
        setCount(pre=>{
            setCount(pre-1)
            let lmg = document.querySelector(".img")                      
            let g = `/Slideshow/image_${pre-1}`+`.gif`
            lmg.src=g;
            if(count == 0) {
                console.log()
            }
        })
    }
    
     /* 
    const sum = () =>{
        setCount(prevalue=>{            
            setCount(prevalue + 1)          
                                                         
            let lmg = document.querySelector(".img")                      
                lmg.src= "/Slideshow/image_" + count +  ".gif"; 
                console.log(count)  
            if (count === 3) {
                let nextBtn =document.querySelector(".summed");
                nextBtn.setAttribute("disabled", "")
                            
                let subBtn =document.querySelector(".subtract");
                subBtn.removeAttribute("disabled", "")
                  
               
                
                
                
                
            }
        })

       
    }
    const sub = ()=>{
        subSetCount(prevalue=>{
           subSetCount(subcount -1 )
           console.log(subcount)
           let lmg = document.querySelector(".img")                      
           lmg.src= "/Slideshow/image_" + subcount +  ".gif";
            if (subcount === 0) {
                console.log("hey")
                let subBtn =document.querySelector(".subtract");
                
              
            }
           
            
        } )
    }


    */

  return (
    <div>
        <div style={{display:"flex"}}>
        <Button onClick={increment}  className="sum">+</Button>
        <img src="/Slideshow/image_0.gif" className="img-fluid img" style={{width:"200px","height":"200px"}}></img>

          <Button onClick={decrement}  className="sub">-</Button>
        </div>
        <p>{count}</p>
        
    </div>
  )
}

export default Tutorial
