import React,{useState} from "react";
import {Button } from "react-bootstrap";
import SlideTwo from "./SlideTwo";
import FinalSlide from "./FinalSlide";
import { useNavigate } from "react-router-dom";

function SlideShow({TestAnswers,setTestanswers,required,setRequired}) {

  const handleTestAnswers = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;    
    setTestanswers({ ...TestAnswers, [name]: value });
    
    
   
  };

  /*const buttons = document.querySelectorAll("input[type='radio']");
  buttons.forEach((button=>{
    console.log(button)
  }))
  */
  

  


  return (    
        
        
        <div className="Container">
          <div className="row justify-content-around py-4">
            <div className="col-sm-6 col-md-6 col-lg-5 col-xxl-6 col-xl-5">
              <img src="/Testing/Test1_Tumor.jpg" className="card-img-size img-fluid" ></img>
              <div className="d-flex justify-content-center my-5">
                <input type="radio" className="ms-3" name="Q1" value="A" onChange={handleTestAnswers} required={required}></input>
                <label>A</label>
              </div>
            </div>
            <div className="col-sm-6 col-md-6 col-lg-5 col-xxl-6 col-xl-5">
              <img src="/Testing/Test1_No_Tumor.jpg" className="card-img-size img-fluid" ></img>
              <div className="d-flex justify-content-center my-5">
                <input type="radio" className="ms-3" name="Q1" value="B" onChange={handleTestAnswers} required={required}></input>
                <label>B</label>
              </div>
            </div>
          </div>          
        </div>       
       
        
   
  );
}

export default SlideShow;
