import React,{useState,useRef} from 'react'
import {Button ,Container} from "react-bootstrap"
import 'bootstrap/dist/css/bootstrap.min.css';


function Tutorial() {
    const[count,setCount]=useState(0)
    //const [subcount,subSetCount] = useState(2)
    function updateSum() {
        for(let i = 0;i < count;i++) {
            increment()
        }
    }

    function increment () {
        setCount(pre => {
            setCount(pre + 1)
            let lmg = document.querySelector(".img")                      
            let g = `/Slideshow/image_${pre+1}`+`.gif`
            lmg.src=g;
            let backBtn =document.querySelector(".sub");            
            backBtn.removeAttribute("disabled", "")
            if (count === 2) {
                let nextBtn =document.querySelector(".sum");
                nextBtn.innerHTML()
                nextBtn.setAttribute("disabled", "")
            } 
            
                       
            
        })
        
        
    }


    function updateSub() {
        for(let i = 0;i < count;i--) {
            decrement()
            
        }
    }

    function decrement() {
        setCount(pre=>{
            setCount(pre-1)
            let lmg = document.querySelector(".img")                      
            let g = `/Slideshow/image_${pre-1}`+`.gif`
            lmg.src=g;
            let nextBtn =document.querySelector(".sum");
            console.log(nextBtn)
            
            nextBtn.removeAttribute("disabled", "") 
            if(count == 1) {
                let backBtn =document.querySelector(".sub");
                backBtn.setAttribute("disabled", "")
                    
            }
        })
    }
    
     /* 
    const sum = () =>{
        setCount(prevalue=>{            
            setCount(prevalue + 1)          
                                                         
            let lmg = document.querySelector(".img")                      
                lmg.src= "/Slideshow/image_" + count +  ".gif"; 
                console.log(count)  
            if (count === 3) {
                let nextBtn =document.querySelector(".summed");
                nextBtn.setAttribute("disabled", "")
                            
                let subBtn =document.querySelector(".subtract");
                subBtn.removeAttribute("disabled", "")
                  
               
                
                
                
                
            }
        })

       
    }
    const sub = ()=>{
        subSetCount(prevalue=>{
           subSetCount(subcount -1 )
           console.log(subcount)
           let lmg = document.querySelector(".img")                      
           lmg.src= "/Slideshow/image_" + subcount +  ".gif";
            if (subcount === 0) {
                console.log("hey")
                let subBtn =document.querySelector(".subtract");
                
              
            }
           
            
        } )
    }


    */

  return (
    <div style={{backgroundColor:"#F0F3F4"}}>
        <div className="container" style={{backgroundColor:"#FFFFFF",width:"100%",height:"100vh"}} >
        <h1 className="text-center pt-5 mb-5 fw-bold">Slideshow</h1>
        <p className="text-center">Showing {count}/3</p>
        <div className="container">            
            <div className="col-6 mx-auto" style={{border:"solid 1px black",height:"500px"}}>
            <img src="/Slideshow/image_0.gif" className="img-fluid img" style={{width:"100%",height:"100%"}}></img>
            </div>
            <div className="col-3 mx-auto mt-5 d-flex">
                <button onClick={decrement} className="previous sub">&laquo; Previous</button>
                <div style={{width:"80px"}}></div>
                <button onClick={increment} className="next sum">Next &raquo;</button>                
            </div>
        </div>
         <div className="container">   
        <div className="col-1 mx-auto mt-6">
            <Button>Next Page</Button>
        </div>
        </div>
        </div>
        
        
    </div>
  )
}

export default Tutorial

