import React, { useState, useNavigate } from "react";
import { Form, Button, Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function SlideTwo() {
  const [checked, setChecked] = useState(false);
  const [checked2, setChecked2] = useState(false);

  const checkBox = () => {
    setChecked((s) => !s);
    if (checked) {
      console.log("now true");
    }
  };
  if (checked) {
    const success = document.getElementById("check");
    if (success.checked) {
      console.log("checked");
    }
  }

  return (
    <div style={{ backgroundColor: "#F0F3F4" }}>
      <div
        className="container"
        style={{ backgroundColor: "#FFFFFF", width: "100%", height: "100vh" }}
      >
        <h1 className="text-center pt-6 mb-5 fw-bold">Training Phase</h1>
        <p className="text-center Ttext">
          Please check the box where you Tumor exsists in the Images
        </p>
        <div className="container">
          <div className="row justify-content-around py-5">
            <div className="col-5">
              <img src="/Testing/Test1_Tumor.jpg" className="img-fluid"></img>
              <div className="d-flex justify-content-center mt-3">
                <input
                  type="radio"
                 
                  className="ms-3"
                  name="Q1"
                  value={values.Q1}
                  onClick={handleFormData("Q1")}
                ></input>
              </div>
            </div>
            <div className="col-5">
              <img
                src="/Testing/Test1_No_Tumor.jpg"
                className="img-fluid"
              ></img>
              <div className="d-flex justify-content-center mt-3">
                <input
                  type="checkbox"
                 
                  className="ms-3"
                  onClick={handleFormData("Q1")}
                ></input>
              </div>
            </div>
          </div>
          <p>{checked}</p>
        </div>
      </div>
    </div>
  );
}

export default SlideTwo;
