import React, {useState,useNa} from 'react'
import {Form , Button ,Container} from "react-bootstrap" 
import 'bootstrap/dist/css/bootstrap.min.css';   


function Testing() {
    const [checked,setChecked]=useState(false);
    const [checked2,setChecked2] = useState(false);

    const checkBox = () => {
        setChecked(s=> !s)
        if(checked) {
            console.log("now true")
        }
    }
    if(checked) {
        const success = document.getElementById("check")
        if (success.checked) {
            console.log("checked")
        }
    }
   


    




  return (
    <div style={{backgroundColor:"#F0F3F4"}}>
    <div className="container" style={{backgroundColor:"#FFFFFF",width:"100%",height:"100vh"}}>
       <h1 className="text-center pt-6 mb-5 fw-bold">Training Phase</h1>
       <p className="text-center Ttext">Please check the box where you Tumor exsists in the Images</p>
       <div className="container">
        <div className="row justify-content-around py-5">
        <div className="col-5" >
            <img src="/Testing/Test1_Tumor.jpg" className="img-fluid"></img>
            <div className="d-flex justify-content-center mt-3">
            <input type="checkbox" id="check" className="ms-3" name="checkbox1" onChange={checkBox}></input>           
            </div>            
        </div>
        <div className="col-5" >
            <img src="/Testing/Test1_No_Tumor.jpg" className="img-fluid"></img>
            <div className="d-flex justify-content-center mt-3">
            <input type="checkbox" id="check" className="ms-3" onChange={checkBox}></input>            
            </div>            
        </div>      
            
        </div>
        <p>{checked}</p>   
           
        </div> 
        <Button type="button" className="py-2 px-5" onClick={()=>{navigate("/Task")}}>Next</Button>



    </div>
    </div>
  )
}

export default Testing
