import React, { useState, useNavigate, useRef, useEffect } from "react";
import { Container, FormCheck } from "react-bootstrap";
import FinalSlide from "./FinalSlide";
import SlideShow from "./SlideShow";
import SlideTwo from "./SlideTwo";
import Task from "../Task";
import reactDom from "react-dom";

function Test() {
  const [page, setPage] = useState(0);
  const [TestAnswers, setTestanswers] = useState({
    Q1: "",
    Q2: "",
    Q3: "",
  });
  const [required, setRequired] = useState(false);

  const TestTitle = [
    "Select which image has tumor",
    "Select which image has lungs",
    "Select which image is correct annotated",
  ];

  const PageDisplay = () => {
    if (page === 0) {
      return (
        <SlideShow
          TestAnswers={TestAnswers}
          setTestanswers={setTestanswers}
          required={required}
          setRequired={setRequired}
        />
      );
    } else if (page === 1) {
      return (
        <SlideTwo
          TestAnswers={TestAnswers}
          setTestanswers={setTestanswers}
        ></SlideTwo>
      );
    } else if (page === 2) {
      return (
        <FinalSlide
          TestAnswers={TestAnswers}
          setTestanswers={setTestanswers}
        ></FinalSlide>
      );
    }
  };

  

  return (
    <Container fluid>
      <div className="test-container">
        <div className="header">
          <h1>Testing</h1>
          <h5>{TestTitle[page]}</h5>
        </div>
        <div className="body" style={{ flex: "60%" }}>
          {PageDisplay()}
        </div>
        <div className="footer">
          <button
            className="previous"
            disabled={page === 0}
            onClick={() => {
              setPage((currentPage) => currentPage - 1);
            }}
          >
            &laquo;Previous
          </button>
          <button
            className="next"
            disabled={page === TestTitle.length}
            onClick={() => {
              if (page === TestTitle.length - 1) {
                if(required === false) {
                  const buttons = document.querySelectorAll("input[type='radio']");
                  console.log()
                }
                
                //alert("Form Submitted");
                //console.log(TestAnswers);
              } else {
                setPage((currentPage) => currentPage + 1);
              }
            }}
          >
            {page === TestTitle.length - 1 ? "Continue" : "Next"}
          </button>
        </div>
      </div>
    </Container>
  );
}

export default Test;
