import React,{Component,useState,useRef} from 'react'
import {Form , Button ,Container} from "react-bootstrap"
import {useNavigate,Link} from "react-router-dom"
import 'bootstrap/dist/css/bootstrap.min.css';




function Questionaries() {

  const [Questions,setQuestions] = useState({   
    Q1:"",
    Q2:"",
    Q3:""
  })

  const handleAnswers = event =>{
    const target = event.target
    const name = target.name
    const value = target.value
    setQuestions({...Questions,[name]:value})
  }


  function submitForm(e) {
    e.preventDefault()
    for (const [key,value] of Object.entries(Questions)) {
       while (value === null || value === "") {        
         const buttons = document.querySelectorAll("input[type='radio']");
         buttons.forEach(button => {          
            if(!button.checked) {
              button.setAttribute("required","")  
              }                    
            })
            return 
       }
              
    }  
            
    console.log(Questions)    
    const submitBtn=document.getElementById("submitBtn")
    submitBtn.addEventListener("click",()=>
      async(e)=>{
        e.preventDefault();
        const {Q1,Q2,Q3} = Questions;
        const res = await fetch("/Questionaries",{
          method:"POST",
          headers:{
            "Content-type" : "application/json"
          },
          body:JSON.stringify({
            Q1,Q2,Q3
          })
        });
        const data = await res.json()

        if (res.status === 422 || !data) {
          window.alert("Questions Failed")
        } else {
          console.log("Questions Successfull")
          navigate("/Description")
        }
      }
     
    )

  
  }

  const navigate = useNavigate()
       
  
  
  return (    
    <>
    <Container className="d-flex align-items-center justify-content-center " style={{minHeight:"100vh"}}>
    <div className="w-100" style={{maxWidth:"1000px"}}>
     <div className="form-container">
     <form method="POST" onSubmit={submitForm}    style={{display:"flex" ,flexDirection:"column"}}>
       <legend className="mb-3">Please fill out this Questionaries?</legend>
       <div className="mb-3">
         <label className="mb-3">What is your income?</label>
           <p><input type="radio" name="Q1" value="200$-400$" onChange={handleAnswers}  className="me-2"/>200$-400$</p>
           <p><input type="radio" name="Q1" value="500$-1000$" onChange={handleAnswers} className="me-2"/>500$-1000$</p>
          <p><input type="radio" name="Q1" value="1000$-2000$" onChange={handleAnswers} className="me-2"/>1000$-2000$</p>
       </div>
       <div className="mb-3">
         <label className="mb-3">What is your level of English Language?</label>
           <p><input type="radio" name="Q2" value="Good" onChange={handleAnswers} className="me-2"/>Good</p>
           <p><input type="radio" name="Q2" value="Bad" onChange={handleAnswers} className="me-2"/>Bad</p>
          <p><input type="radio" name="Q2" value="Satisfied" onChange={handleAnswers} className="me-2"/>Satisfied</p>
       </div>
       <div className="mb-3">
         <label className="mb-3">What is your level of English Language?</label>
           <p><input type="radio" name="Q3" value="Good" onChange={handleAnswers} className="me-2"/>Good</p>
           <p><input type="radio" name="Q3" value="Bad" onChange={handleAnswers} className="me-2"/>Bad</p>
          <p><input type="radio" name="Q3" value="Satisfied" onChange={handleAnswers} className="me-2"/>Satisfied</p>
       </div>
       <Button type="submit" className="mt-4" id="submitBtn">Submit</Button>
      
       
     </form>
     
</div>






      </div>



    </Container>
   </>
  )
}

export default Questionaries
