import React,{useState} from 'react'

function Tutorial() {
    const[count,setCount]=useState(1)

    const sum = () =>{
        setCount(prevalue=>{
            setCount(count +1)
        })

        const sub = ()=>{
            setCount(prevalue=>{
                setCount(count -1)
            } )
        }
    }



  return (
    <div>
        <div style={{display:"flex"}}>
        <button onClick={sum}>+</button>
            {count}
        <button onClick={sub}>-</button>
        </div>
        
    </div>
  )
}

export default Tutorial
